async function setupDatabase() {
  try {
    console.log('ุจุฏุก ุฅุนุฏุงุฏ ูุงุนุฏุฉ ุงูุจูุงูุงุช...');

    // ุฅูุดุงุก ูุงุนุฏุฉ ุงูุจูุงูุงุช ุฅุฐุง ูู ุชูู ููุฌูุฏุฉ
    await pool.query(`CREATE DATABASE coptic_patriarchs`).catch(() => {
      console.log('ูุงุนุฏุฉ ุงูุจูุงูุงุช ููุฌูุฏุฉ ุจุงููุนู ุฃู ุชู ุฅูุดุงุคูุง');
    });

    // ุฅูุดุงุก ุฌุฏูู ุงูุฌูุณุงุช
    await pool.query(`
      CREATE TABLE IF NOT EXISTS sessions (
        sid VARCHAR PRIMARY KEY NOT NULL,
        sess TEXT NOT NULL,
        expire TIMESTAMP(6) NOT NULL
      );
    `);

    // ุฅูุดุงุก ููุฑุณ ููุฌูุณุงุช
    await pool.query(`
      CREATE INDEX IF NOT EXISTS "IDX_session_expire" ON sessions(expire);
    `);

    // ุฅูุดุงุก ุฌุฏูู ุงููุณุชุฎุฏููู
    await pool.query(`
      CREATE TABLE IF NOT EXISTS users (
        id TEXT PRIMARY KEY NOT NULL,
        email TEXT UNIQUE,
        first_name TEXT,
        last_name TEXT,
        profile_image_url TEXT,
        created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
        updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
      );
    `);

    // ุฅูุดุงุก ุฌุฏูู ุงูุจุทุงุฑูุฉ
    await pool.query(`
      CREATE TABLE IF NOT EXISTS patriarchs (
        id SERIAL PRIMARY KEY,
        name TEXT NOT NULL,
        "arabicName" TEXT,
        "orderNumber" INTEGER NOT NULL,
        "startYear" INTEGER NOT NULL,
        "endYear" INTEGER,
        era TEXT NOT NULL,
        contributions TEXT NOT NULL,
        biography TEXT,
        "heresiesFought" TEXT[] NOT NULL,
        active BOOLEAN NOT NULL DEFAULT true,
        "createdAt" TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
        "updatedAt" TIMESTAMP DEFAULT CURRENT_TIMESTAMP
      );
    `);

    console.log('ุชู ุฅูุดุงุก ุงูุฌุฏุงูู ุจูุฌุงุญ!');
    console.log('ููููู ุงูุขู ุชุดุบูู complete_patriarchs_data.js ูุงุณุชูุฑุงุฏ ุงูุจูุงูุงุช');

  } catch (error) {
    console.error('ุฎุทุฃ ูู ุฅุนุฏุงุฏ ูุงุนุฏุฉ ุงูุจูุงูุงุช:', error);
