import pkg from "pg";
import { drizzle } from "drizzle-orm/node-postgres";
import * as schema from "@shared/schema";
import dotenv from "dotenv";

// Load environment variables from .env.local
dotenv.config({ path: '.env.local' });

const { Pool } = pkg;

if (!process.env.DATABASE_URL) {
  throw new Error("DATABASE_URL must be set. Did you forget to provision a database?");
}

// Enhanced connection configuration for both online and local PostgreSQL
const connectionConfig = {
  connectionString: process.env.DATABASE_URL,
  max: 20,
  idleTimeoutMillis: 30000,
  connectionTimeoutMillis: 5000,
  // Enhanced SSL configuration for online/offline compatibility
  ssl: process.env.DATABASE_URL.includes('localhost') || process.env.DATABASE_URL.includes('127.0.0.1') 
    ? false 
    : process.env.NODE_ENV === 'production' 
      ? { rejectUnauthorized: false } 
      : { rejectUnauthorized: false },
  allowExitOnIdle: true,
  // Additional configuration for connection stability
  keepAlive: true,
  keepAliveInitialDelayMillis: 10000,
};

export const pool = new Pool(connectionConfig);

// Handle connection errors gracefully
pool.on('error', (err) => {
  console.error('Unexpected error on idle client', err);
  // Don't exit process in development, log and continue
  if (process.env.NODE_ENV !== 'development') {
    process.exit(-1);
  }
});

// Test connection on startup
pool.on('connect', () => {
  console.log('โ Successfully connected to PostgreSQL database');
});

export const db = drizzle({ client: pool, schema });

server/gemini.ts.content:
import { GoogleGenAI, Modality } from "@google/genai";
import * as fs from "fs";
import { storage } from './storage.js';

let geminiClient: GoogleGenAI | null = null;

export async function getGeminiClient(): Promise<GoogleGenAI | null> {
  try {
    const apiKey = (await storage.getSetting('gemini_api_key'))?.value;
    if (!apiKey) {
      console.log('No Gemini API key found');
      return null;
    }

    if (!geminiClient) {
      geminiClient = new GoogleGenAI({ apiKey });
    }

    return geminiClient;
  } catch (error) {
    console.error('Error initializing Gemini client:', error);
    return null;
  }
}

export async function summarizeArticle(text: string): Promise<string> {
  const client = await getGeminiClient();
  if (!client) {
    throw new Error('Gemini AI client not available');
  }

  const prompt = `Please summarize the following text concisely while maintaining key points:\n\n${text}`;

  const response = await client.models.generateContent({
    model: "gemini-2.5-flash",
    contents: prompt,
  });

  return response.text || "Something went wrong";
}

export interface Sentiment {
  rating: number;
  confidence: number;
}

export async function analyzeSentiment(text: string): Promise<Sentiment> {
  try {
    const client = await getGeminiClient();
    if (!client) {
      throw new Error('Gemini AI client not available');
    }

    const systemPrompt = `You are a sentiment analysis expert. 
Analyze the sentiment of the text and provide a rating
from 1 to 5 stars and a confidence score between 0 and 1.
Respond with JSON in this format: 
{'rating': number, 'confidence': number}`;

    const response = await client.models.generateContent({
      model: "gemini-2.5-pro",
      config: {
        systemInstruction: systemPrompt,
        responseMimeType: "application/json",
        responseSchema: {
          type: "object",
          properties: {
            rating: { type: "number" },
            confidence: { type: "number" },
          },
          required: ["rating", "confidence"],
        },
      },
      contents: text,
    });

    const rawJson = response.text;

    if (rawJson) {
      const data: Sentiment = JSON.parse(rawJson);
      return data;
    } else {
      throw new Error("Empty response from model");
    }
  } catch (error) {
    throw new Error(`Failed to analyze sentiment: ${error}`);
  }
}

export async function analyzeImage(jpegImagePath: string): Promise<string> {
  const client = await getGeminiClient();
  if (!client) {
    throw new Error('Gemini AI client not available');
  }

  const imageBytes = fs.readFileSync(jpegImagePath);

  const contents = [
    {
      inlineData: {
        data: imageBytes.toString("base64"),
        mimeType: "image/jpeg",
      },
    },
    `Analyze this image in detail and describe its key elements, context,
and any notable aspects.`,
  ];

  const response = await client.models.generateContent({
    model: "gemini-2.5-pro",
    contents: contents,
  });

  return response.text || "";
}

export async function analyzeVideo(mp4VideoPath: string): Promise<string> {
  const client = await getGeminiClient();
  if (!client) {
    throw new Error('Gemini AI client not available');
  }

  const videoBytes = fs.readFileSync(mp4VideoPath);

  const contents = [
    {
      inlineData: {
        data: videoBytes.toString("base64"),
        mimeType: "video/mp4",
      },
    },
    `Analyze this video in detail and describe its key elements, context,
    and any notable aspects.`,
  ];

  const response = await client.models.generateContent({
    model: "gemini-2.5-pro",
    contents: contents,
  });

  return response.text || "";
}

export async function generateImage(
  prompt: string,
  imagePath: string,
): Promise<void> {
  try {
    const client = await getGeminiClient();
    if (!client) {
      throw new Error('Gemini AI client not available');
    }

    const response = await client.models.generateContent({
      model: "gemini-2.0-flash-preview-image-generation",
      contents: [{ role: "user", parts: [{ text: prompt }] }],
      config: {
        responseModalities: [Modality.TEXT, Modality.IMAGE],
      },
    });

    const candidates = response.candidates;
    if (!candidates || candidates.length === 0) {
      return;
    }

    const content = candidates[0].content;
    if (!content || !content.parts) {
      return;
    }

    for (const part of content.parts) {
      if (part.text) {
        console.log(part.text);
      } else if (part.inlineData && part.inlineData.data) {
        const imageData = Buffer.from(part.inlineData.data, "base64");
        fs.writeFileSync(imagePath, imageData);
        console.log(`Image saved as ${imagePath}`);
      }
    }
  } catch (error) {
    throw new Error(`Failed to generate image: ${error}`);
  }
}

export async function generateAIContent(input: string): Promise<string> {
  const client = await getGeminiClient();
  if (!client) {
    throw new Error('Gemini AI ุบูุฑ ูุชุงุญ');
  }

  const response = await client.models.generateContent({
    model: "gemini-2.5-flash",
    contents: input,
    config: {
      temperature: 0.7,
      maxOutputTokens: 1500,
    }
  });

  if (!response.candidates?.[0]?.content?.parts?.[0]?.text) {
    throw new Error('Invalid response from Gemini API');
  }

  const responseText = response.candidates[0].content.parts[0].text;
  return responseText.trim();
}

export async function testGeminiConnection(): Promise<boolean> {
  try {
    const client = await getGeminiClient();
    if (!client) return false;

    const response = await client.models.generateContent({
      model: "gemini-2.5-flash",
      contents: "ุงุฎุชุจุงุฑ ุงูุงุชุตุงู",
    });

    return !!response.candidates?.[0]?.content?.parts?.[0]?.text;
  } catch (error) {
    console.error("Gemini connection test failed:", error);
    return false;
  }
}

export async function askPatriarch(question: string): Promise<string> {
  const client = await getGeminiClient();
  if (!client) {
    return "ุนุฐุฑุงูุ ูู ูุชู ุชูููู ููุชุงุญ API ุงูุฎุงุต ุจู Gemini. ูุฑุฌู ุฅุถุงูุฉ ุงูููุชุงุญ ูู ุตูุญุฉ ุงูุฅุฏุงุฑุฉ.";
  }

