      if (fs.existsSync(csvPath)) {
        const csvContent = fs.readFileSync(csvPath, 'utf-8');
        externalSources += `\n\nูุตุงุฏุฑ ุฅุถุงููุฉ ูู ููู ุงูุจูุงูุงุช ุงูุชุงุฑูุฎูุฉ:\n${csvContent.substring(0, 4000)}\n`;
      }
    } catch (error) {
      console.log('CSV file reading failed, continuing without it');
    }

    // 2. Read additional text files if available
    try {
      const fs = await import('fs');
      const path = await import('path');
      const assetsDir = path.join(process.cwd(), 'attached_assets');
      if (fs.existsSync(assetsDir)) {
        const files = fs.readdirSync(assetsDir);
        const textFiles = files.filter(file => file.endsWith('.txt'));

        for (const file of textFiles.slice(0, 3)) { // Limit to 3 files to avoid token limits
          try {
            const content = fs.readFileSync(path.join(assetsDir, file), 'utf-8');
            externalSources += `\n\nูู ููู ${file}:\n${content.substring(0, 2000)}\n`;
          } catch (error) {
            console.log(`Failed to read ${file}`);
          }
        }
      }
    } catch (error) {
      console.log('External files reading failed, continuing without them');
    }

    // Format patriarch data for context, focusing on Arabic names and content
    const contextData = patriarchs.slice(0, 30).map(p => {
      let heresiesFought = [];
      try {
        heresiesFought = typeof p.heresiesFought === 'string' 
          ? JSON.parse(p.heresiesFought) 
          : p.heresiesFought || [];
      } catch (e) {
        heresiesFought = [];
      }

      return `${p.orderNumber}. ${p.arabicName || p.name} (${p.startYear}-${p.endYear || 'ุงูุขู'}) - ุงูุนุตุฑ: ${p.era} - ุงููุณุงููุงุช: ${(p.contributions || '').substring(0, 200)} - ุงูุจุฏุน ุงููุญุงุฑุจุฉ: ${heresiesFought.join(', ')} - ุงูุณูุฑุฉ: ${(p.biography || '').substring(0, 200)}`;
    }).join('\n');

    const prompt = `ุฃูุช ุฎุจูุฑ ูู ุชุงุฑูุฎ ุงููููุณุฉ ุงููุจุทูุฉ ุงูุฃุฑุซูุฐูุณูุฉ ูุงูุจุทุงุฑูุฉ. ูุฏูู ูุนุฑูุฉ ุดุงููุฉ ุจุงูู 118 ุจุทุฑูุฑูุงู ูู ุงููุฏูุณ ูุฑูุณ ุงูุฑุณูู ุฅูู ุงูุจุงุจุง ุชูุงุถุฑูุณ ุงูุซุงูู ุงูุญุงูู.

ุงูุณุคุงู: ${question}

ูุนูููุงุช ุงูุจุทุงุฑูุฉ ุงููุชุงุญุฉ:
