      patriarch.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
      patriarch.arabicName?.toLowerCase().includes(searchQuery.toLowerCase());

    const matchesEra = selectedEra === "all" || patriarch.era === selectedEra;

